<script>export let id = 'range';
export let label = 'Range';
export let min;
export let max;
export let value;
export let step;
export let size;
let inputClass = 'w-full bg-gray-200 rounded-lg appearance-none cursor-pointer dark:bg-gray-700 ';
if (size === 'small') {
    inputClass += 'h-1 mb-6 range-sm';
}
else if (size === 'large') {
    inputClass += 'h-3 range-lg';
}
else {
    inputClass += 'h-2 mb-6';
}
</script>

<label for={id} class="block mb-2 text-sm font-medium text-gray-900 dark:text-gray-300">{label}</label>
<input {id} name={id} type="range" {min} {max} bind:value {step} class={inputClass} {...$$restProps} />
